document.addEventListener("DOMContentLoaded",function(){const o=document.getElementById("searchConversations"),s=document.querySelectorAll("#loadConversation");o.addEventListener("input",function(){const t=this.value.toLowerCase();s.forEach(e=>{const n=e.querySelector("h5").textContent.toLowerCase(),l=e.querySelector(".line-clamp").textContent.toLowerCase();n.includes(t)?e.style.display="":e.style.display="none",n.includes(t)||l.includes(t)?e.style.display="":e.style.display="none"})})});
